mapper.select:
select d1.trip_detail_id, u1.user_id, d1.image_path, s1.sido_name, d1.title, d1.start_date, d1.end_date, u1.nickname, d1.max_capacity, d1.view, d1.created_at, timestampdiff(minute, d1.created_at, now()) as create_time from trip_detail d1 join user u1 on d1.user_id = u1.user_id join sido s1 on d1.sido_code = s1.sido_code
and d1.start_date <= #{date} and d1.end_date >= #{date}
and d1.title like CONCAT('%', #{keyword}, '%')
and s1.sido_code = #{sidoCode}
and d1.start_date > curdate()
order by create_time

select d1.trip_detail_id, u1.user_id, d1.image_path, s1.sido_name, d1.title, d1.start_date, d1.end_date, u1.nickname, d1.max_capacity, d1.view, d1.created_at, timestampdiff(minute, d1.created_at, now()) as create_time from trip_detail d1 join user u1 on d1.user_id = u1.user_id join sido s1 on d1.sido_code = s1.sido_code
and d1.start_date <= #{date} and d1.end_date >= #{date}
and d1.title like CONCAT('%', #{keyword}, '%')
and s1.sido_code = #{sidoCode}
and d1.end_date < curdate()
order by create_time
